.container {
    max-width: 100%;
    min-height: rem(0);

    &__inner {
        max-width: $max-width;
        margin: rem(20) auto;
        padding: rem(20);
        min-height: rem(500);
        display: flex;
        justify-content: center;
        align-items: center;
        flex-wrap: wrap;

        @include media-query(tablet) {
            justify-content: flex-start;
        }

        & > * {
            flex: 1 rem(270);
            justify-self: flex-start;
            align-self: flex-start;
            max-width: rem(280);
            min-height: rem(350);
        }
    }

    &__details {
        max-width: $max-width;
        min-height: rem(450);
        margin: rem(0) auto;
        display: flex;
        flex-direction: column;

        @include media-query(tablet) {
            flex-direction: row;
            align-items: center;
        }

        & > * {
            flex: 1;
            padding: rem(20);
        }
    }
}

.country {

    &__info {

        h2 {
            font-size: rem(30);
            font-weight: $weight-300;
        }
        @include media-query(desktop) {
            margin-left: rem(100);
        }
    }
    
    &__details {
        display: flex;
        flex-direction: column;

        @include media-query(tablet) {
            flex-direction: row;
            justify-content: space-between;
        }

        & > * {
            margin: rem(10) rem(0);
        }

        li {
            font-weight: $weight-200;
            line-height: rem(30);

            span {
                font-weight: $weight-100;
            }
        }
    }

    &__header {
        margin: rem(20) rem(0);
    }

    &__border {
        display: flex;
        flex-wrap: wrap;
        margin: rem(15) rem(0);

        & > * {
            background-color: var(--color-100);
            box-shadow: var(--color-400);
            padding: rem(5) rem(10);
            margin: rem(5) rem(10) rem(5) rem(0);

            &:hover {
                transition: 0.5s ease-in-out;
                background-color: var(--color-600);
            }
        }
    }
}

