.card {
    background-color: var(--color-100);
    box-shadow: $shadow;
    border-radius: rem(5);
    cursor: pointer;
    margin: rem(20);

    &__image img {
        border-radius: rem(5) rem(5) rem(0) rem(0);
    }
    &__title {
        padding: rem(20) rem(20) rem(10);
    }
    &__info {
        padding: rem(5) rem(20) rem(30);
        p {
            font-weight: $weight-200;
            line-height: rem(25);
        }
        span {
            font-weight: $weight-100;
        }
    }

    &:hover {
        transition: 0.5s;
        background-color: var(--color-600);
        transform: translateY(rem(-10)); 
    }
}

